package space.co.work;

public class eodlq {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int aVar=20;
			aVar+=10;
			System.out.println("aVar+=10 ->" +aVar);
			aVar-=10;
			System.out.println("aVar-=10 ->" +aVar);
			aVar*=10;
			System.out.println("aVar*=10 ->" +aVar);
			aVar/=10;
			System.out.println("aVar/=10 ->" +aVar);
			aVar%=10;
			System.out.println("aVar%=10 ->" +aVar);

	}

}
